#include <bits/stdc++.h>
using namespace std;

string zeronum = "";
string ninenum = "";
int ninelen = 1024;
int zerolen = 1024;

vector<string> num;

int t;
long n;
long long s;

bool evaluate(long d, long long s){
	
	if((s%2) && !(d%2)) return false;
	if(s > d*9) return false;
	if((s == 1) && (d > 1)) return false;

	bool has_mid = (d%2);
	
	string tmp;

	int cap = 9216;

	while(s > 1){
		while(cap*2 > s) {cap /= 2; ninelen /= 2;}

		if(s > 17){
			if(s >= cap){
				num.push_back( ninenum.substr(0, ninelen) );
				s -= cap*2;
				////
				if(d < ninelen*2) return false;
				////
				d -= ninelen*2;
			}
		}
		else{
			if(num.size()>0)
				if((s < 10) & (d==1)) break;
	
			tmp = (s/2)+'0';
			num.push_back( tmp );
			s = s%2;
			////
			if(d < 2) return false;
			////
			d -= 2;
		}
		//if(s == 0) break;
	}

	while(d > 1){
		while(d < zerolen*2){zerolen /= 2;}
		num.push_back( zeronum.substr(0, zerolen) );
		d -= zerolen*2;
	}

	for(long i = 0; i<=num.size()-1; i++) cout << num[i];

	if(has_mid) cout << s; 

	for(long i = num.size()-1; i>=0; i--) cout << num[i];
	return true;
}

int main(){
	cin >> t;
	while(t-- > 0){
		ninelen = 1024;
		zerolen = 1024;

		cin >> n >> s;

		if(n == 1){
			if(s < 10) cout << s;
			else cout << "-1";
		}
		else{
			if(!evaluate(n, s)) cout << "-1";
			num.clear();
		}
		cout << "\n";
	}
}